INSERT INTO `jos_content` VALUES(92, 'BOJ testimonial 13', 'boj-testimonial-13', '', '<p>"These guys are top notch. I came here just to write a review. In our day, people are getting more and more self-serving. A company does very well when they give good customer service."</p>\r\n<p class="review-by">Review on Bestofjoomla.com <span>05.02.2008</span></p>', '', 1, 1, 0, 42, '2008-12-17 13:56:01', 63, '', '2009-02-04 14:41:35', 63, 0, '0000-00-00 00:00:00', '2008-12-17 13:56:01', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 13, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES(93, 'BOJ testimonial 8', 'boj-testimonial-8', '', '<p>"I''d echo many of the existing comments here - great support from these guys, and I only bought one template (I only have one site). Highly recommended!"</p>\r\n<p class="review-by">Review on Bestofjoomla.com <span>08.05.2008</span></p>', '', 1, 1, 0, 42, '2008-12-17 13:56:01', 63, '', '2009-02-04 14:11:57', 63, 0, '0000-00-00 00:00:00', '2008-12-17 13:56:01', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 0, 0, 8, '', '', 0, 2, '');
INSERT INTO `jos_content` VALUES(94, 'BOJ testimonial 9', 'boj-testimonial-9', '', '<p>"Frequent template releases, outstanding customer support, good looking templates, tons of features.\r\nJoomlaJunkie is definitely tops on my list of favorites."</p>\r\n<p class="review-by">Review on Bestofjoomla.com <span>18.04.2008</span></p>', '', 1, 1, 0, 42, '2008-12-17 13:56:01', 63, '', '2009-02-04 14:17:33', 63, 0, '0000-00-00 00:00:00', '2008-12-17 13:56:01', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 9, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES(95, 'BOJ testimonial 18', 'boj-testimonial-18', '', '<p>"I have had dealings with many web companies over the years and I can say unequivocally this has been the best, the easiest, the most stress-free experience ever! And we’re not even on the same continent! The JoomlaJunkie guys are in the South Africa and I’m in Australia and yet despite a nine hour time difference their response to my needs has been like lightning."</p>\r\n<p class="review-by">Review by <span>Munaiba Khan – The Copywriter Online</span></p>', '', 1, 1, 0, 42, '2008-12-17 13:56:01', 63, '', '2009-02-04 14:55:12', 63, 0, '0000-00-00 00:00:00', '2008-12-17 13:56:01', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 18, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES(96, 'BOJ testimonial 16', 'boj-testimonial-16', '', '<p>"All I can say is WOW!!! These guys are absolutely phenomenal. Their work is awesome and very precise. Although there are a lot of theme providers out there, Joomla Junkie far surpasses them with their level of detail and level of service."</p>\r\n<p class="review-by">Review by <span>Nick Ferguson - Beyond Ministries</span></p>', '', 1, 1, 0, 42, '2008-12-17 13:56:01', 63, '', '2009-02-04 14:43:52', 63, 0, '0000-00-00 00:00:00', '2008-12-17 13:56:01', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 16, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES(97, 'BOJ testimonial 14', 'boj-testimonial-14', '', '<p>"I bought the template Absolute Flush from them and needed help. With others mostly you have to wait days and days, or they do not answer at all. JoomlaJunkie needed a few minutes for doing good work for me. Absolutely professionals. I can recommend them."</p>\r\n<p class="review-by">Review on Bestofjoomla.com <span>17.01.2008</span></p>', '', 1, 1, 0, 42, '2008-12-17 13:56:01', 63, '', '2009-02-04 14:42:19', 63, 0, '0000-00-00 00:00:00', '2008-12-17 13:56:01', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 14, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES(98, 'BOJ testimonial 20', 'boj-testimonial-20', '', '<p>"First of all, and most importantly, the templates are simply awesome. Most are packed full of module space, making my job much easier. Not only that, but JoomlaJunkie also includes, in many templates, bells and whistles like dynamic font-sizers, color changers, page width sizers, and column location changers, allowing the end-users of my sites to customize their individual experience. Believe me, these things make a HUGE difference to my customers!"</p>\r\n<p class="review-by">Review on Bestofjoomla.com</p>', '', 1, 1, 0, 42, '2008-12-17 13:56:01', 63, '', '2009-02-04 15:08:35', 63, 0, '0000-00-00 00:00:00', '2008-12-17 13:56:01', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 20, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES(99, 'BOJ testimonial 12', 'boj-testimonial-12', '', '<p>"All I can say is WOW!!! These guys are absolutely phenomenal. Their work is awesome and very precise. Although there are a lot of theme providers out there, Joomla Junkie far surpasses them with their level of detail and level of service. On top of their service, they are incredibly generous with their offering and as a result of this company, I have truly been blessed...I find this company to be remarkable in everything they do and it shows down to the very last detail in their work and their work ethic."</p>\r\n<p class="review-by">Review on Bestofjoomla.com <span>26.02.2008</span></p>', '', 1, 1, 0, 42, '2008-12-17 13:56:01', 63, '', '2009-02-04 14:41:08', 63, 0, '0000-00-00 00:00:00', '2008-12-17 13:56:01', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 12, '', '', 0, 2, 'robots=\nauthor=');
