INSERT INTO `jos_content` VALUES(101, 'BOJ testimonial 17', 'boj-testimonial-17', '', '<p>"I spent plenty of time on the net, looking for beautiful Joomla templates.  I must have visited more than 200 sites.  One day I stumbled upon JoomlaJunkie.  I''m certainly glad that I did."</p>\r\n<p class="review-by">Review by <span>Rick Stoffiere - MyPeaceSpace</span></p>', '', 1, 1, 0, 42, '2008-12-17 13:56:01', 63, '', '2009-02-04 14:44:33', 63, 0, '0000-00-00 00:00:00', '2008-12-17 13:56:01', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 17, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES(102, 'BOJ testimonial 19', 'boj-testimonial-19', '', '<p>"I first started by downloading and trying one of their free templates. It was at that point that I began looking further into their commercial offerings. After seeing all the templates had to offer, I decided to purchase a Developer''s license. Let me tell you - that was the best money I have spent on anything relating to Joomla! There''s the old saying that you get what you pay for, but with JoomlaJunkie I''ve gotten far MORE than I paid."</p>\r\n<p class="review-by">Review on Bestofjoomla.com</p>', '', 1, 1, 0, 42, '2008-12-17 13:56:01', 63, '', '2009-02-04 15:07:45', 63, 0, '0000-00-00 00:00:00', '2008-12-17 13:56:01', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 19, '', '', 0, 2, 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES(103, 'BOJ testimonial 11', 'boj-testimonial-11', '', '<p>"Joomlajunkie has very beautiful, clean and functional templates. Their support is the best I''ve seen and whenever I have a problem they always solve it on time. I''m very satisfied with Joomlajunkie and would definitely recommend it to you!!!"</p>\r\n<p class="review-by">Review on Bestofjoomla.com <span>03.04.2008</span></p>', '', 1, 1, 0, 42, '2008-12-17 13:56:01', 63, '', '2009-02-04 14:26:55', 63, 0, '0000-00-00 00:00:00', '2008-12-17 13:56:01', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', 1, 0, 11, '', '', 0, 2, 'robots=\nauthor=');
